var http = require('http');
var log4tongji = require('../lib/log4tongji');

log4tongji.addAppender(log4tongji.fileAppender('poi'),'poi');		// 打开文件
var poi = log4tongji.getLogger('poi');  //获取一个日志记录器对象



 http.Server(function(req, res) {
 	poi.info({serverName:'poi',msg:"poigid:'111',arcode:'33'"});		//触发一个log事件
    res.writeHead(200);
    res.end("hello world\n");
  }).listen(8000);